import axios from "axios";
import FormData from "form-data";
import https from "https";

export const code = async (inputs) => {
  try {
    const presentationUrl = inputs.presentationLink;

    // Download PPTX
    const response = await axios.get(presentationUrl, { responseType: "arraybuffer" });

    // Build form
    const form = new FormData();
    form.append("File", response.data, {
      filename: "presentation.pptx",
      contentType:
        "application/vnd.openxmlformats-officedocument.presentationml.presentation",
    });
    form.append("extractText", "false");
    form.append("extractImage", "true");
    form.append("extractAll", "false");

    // ✅ IMPORTANT: allow self-signed / internal CA cert
    const httpsAgent = new https.Agent({ rejectUnauthorized: false });

    const uploadResponse = await axios.post(
      "https://pptx-extractor.duckdns.org/extract-pptx",
      form,
      {
        httpsAgent,
        headers: {
          ...form.getHeaders(),
          accept: "application/json",
        },
        maxBodyLength: Infinity,
        maxContentLength: Infinity,
        timeout: 120000,
      }
    );

    return uploadResponse.data;
  } catch (error) {
    const msg =
      error?.response?.data?.detail ||
      error?.response?.data ||
      error?.message ||
      "Unknown error";
    return { success: false, error: msg };
  }
};

