export const code = async (inputs: any): Promise<{ currentMonth: string; previousMonth: string; year: number }> => {
  const date = new Date();

  // Options for retrieving the month name in full (e.g., "October")
  const options: Intl.DateTimeFormatOptions = { month: 'long' };

  // Retrieve the current month name
  const currentMonth: string = date.toLocaleString('default', options);

  // Calculate the previous month correctly by adjusting to the 1st of the month first
  const previousMonthDate = new Date(date);
  previousMonthDate.setDate(1); // Set to the 1st of the current month to avoid issues with the day
  previousMonthDate.setMonth(previousMonthDate.getMonth() - 1); // Move to the previous month
  const previousMonth: string = previousMonthDate.toLocaleString('default', options);

  // Retrieve the full year (e.g., 2024)
  const year: number = date.getFullYear();

  return { currentMonth, previousMonth, year };
};
